import React from 'react';
import {withRouter} from 'react-router';

class WordComponent extends React.Children {
    constructor(props){
        super(props);
        this.images = null;
        this.sounds = null;
    }
    componentDidMount() {
        this.images = this.importAll(require.context('../../assets/images/wordImages', false, /\.jpeg&/))
        this.sounds = this.importAll(require.context('../../assets/audioFiles/pretest_talker', false, /\.wav&/))
    }

    importAll(r) {
        let images = {};
        // eslint-disable-next-line array-callback-return
        r.keys().map((item, index) => {images[item.replace('','')] = r(item) });
        return images;
    }

    render() {
        console.log(this.images);
        console.log(this.sounds);

        return (
            <div>
            </div>
        );
    }
}

export const WordComponentView = withRouter(WordComponent);
