import React from 'react';
import {withRouter} from 'react-router';
import '../../assets/css/landingPage.css'
import logo from '../../assets/images/logo.svg'
import { WhiteTextField } from '../../assets/inputTypes/inputTypes'
import {makeStyles, createMuiTheme} from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { ThemeProvider } from '@material-ui/styles';

const theme = createMuiTheme({
    palette: {
        primary: {
        main: '#fff',
        },
    },
});

function validate(name) {

    return {
      name: name.length === 0,
    };
  }

class LandingPage extends React.Component {
    constructor(props){
        super(props);
        this.state = { buttonDisabled: true, name: '' };
    }

    handleNameChange = evt => {
        this.setState({ name: evt.target.value });
      };

    canBeSubmitted() {
        const errors = validate(this.state.name);
        const isDisabled = Object.keys(errors).some(x => errors[x]);
        return !isDisabled;
    }

    render() {
        const errors = validate(this.state.name);
        const isDisabled = Object.keys(errors).some(x => errors[x]);
        return (
            <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <p className='inputs'>
                Welcome to the Science Spelling Training.<br/><br/>
                Please enter your name:
                <div className='name-input-container'>
                    <WhiteTextField id="name-input" value={this.state.name} fullWidth label="Name" variant="outlined" onChange={this.handleChange.bind(this)}/>
                </div>
                </p>

                <ThemeProvider theme={theme}>
                <Button variant="contained" color="primary" size='large' disabled={isDisabled}>
                    Continue
                </Button>
            </ThemeProvider>
            </header>
            </div>
        );
    }
}

export const LandingPageView = withRouter(LandingPage);
