import React from 'react';
import {withRouter} from 'react-router';
import {MediaComponentLayout} from './mediaComponet/layout'
import {wordList} from '../../wordList'

class WordComponent extends React.Component {
    constructor(props){
        super(props);
        this.currentWord = 0;
    }
    render() {
        console.log(wordList[this.currentWord])
        return (
            <div>
                <MediaComponentLayout word={wordList[this.currentWord]}/>
                <div className='name-input-container'>
                        <WhiteTextField id="name-input" value={this.state.name}
                        fullWidth label="Name" variant="outlined" 
                        onChange={this.handleNameChange.bind(this)} onKeyPress={this.handleEnter.bind(this)}/>
                    </div>

                    <ThemeProvider theme={defaultTheme}>
                        <Button variant="contained" color="primary"
                        size='large' disabled={!isDisabled}
                        onClick={this.handleButtonClick.bind(this)}>
                            Continue
                        </Button>
                </ThemeProvider>
            </div>
        );
    }
}

export const WordComponentView = withRouter(WordComponent);
