import React from 'react';
import {withRouter} from 'react-router';
import Button from '@material-ui/core/Button';
import { ThemeProvider } from '@material-ui/styles';
import { defaultTheme } from "../../assets/inputTypes/themes.js";
import Sound from 'react-sound';
import '../../assets/css/app.css';
import '../../assets/css/introduction.css';
import instructions from '../../assets/audioFiles/instructions/pretest_instructions.mp4';

class IntroTextComponent extends React.Component {
    constructor(props){
        super(props);
        this.state = { playingStatus: Sound.status.PLAYING, continue: false }
    }

    handleButtonClick(){
        this.props.intro = false;
    }

    handleIntroFinished() {
        this.setState({ playingStatus: Sound.status.STOPPED, continue: true });
    }

    render() {
        return (
            <div className="App">
                <div className="intro-container">
                    <p>
                        You will see a picture and hear a word. You will 
                        be asked to spell the word using the computer keyboard. 
                        If you are not sure how to spell the word, just make your 
                        best guess. You will only be able to hear the word one time,
                        so be sure to pay attention. Is this loud enough?
                    </p>

                    <Sound
                        url={instructions}
                        playStatus={this.state.playingStatus}
                        onFinishedPlaying={this.handleIntroFinished.bind(this)}
                    />

                    <ThemeProvider theme={defaultTheme}>
                        <Button variant="contained" color="primary"
                        size='large' disabled={!this.state.continue}
                        onClick={this.handleButtonClick.bind(this)}>
                            Continue
                        </Button>
                    </ThemeProvider>
                </div>
            </div>
        );
    }
}

export const IntroTextView = withRouter(IntroTextComponent);
