import React from 'react';
import {withRouter} from 'react-router';
import logo from '../../assets/images/logo.svg'
import { WhiteTextField } from '../../assets/inputTypes/inputTypes'
import { createMuiTheme} from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { ThemeProvider } from '@material-ui/styles';
import '../../assets/css/landingPage.css'

const theme = createMuiTheme({
    palette: {
        primary: {
        main: '#fff',
        },
    },
});

function validate(name) {

    return {
      name: /^([a-zA-Z ])+$/g.test(name),
    };
  }

class LandingPage extends React.Component {
    constructor(props){
        super(props);
        this.state = { name: '' };
    }

    handleNameChange(event) {
        this.setState({ name: event.target.value });
    };

    handleEnter(event) {
        if (event.key === 'Enter') {
            this.setState({ name: event.target.value });
            console.log(this.state.name)
        }
    }

    handleButtonClick() {
        console.log(this.state.name)
    }

    canBeSubmitted() {
        const errors = validate(this.state.name);
        const isDisabled = Object.keys(errors).some(x => errors[x]);
        return !isDisabled;
    }

    render() {
        const errors = validate(this.state.name);
        const isDisabled = Object.keys(errors).some(x => errors[x]);
        return (
            <div className="App">
                <header className="App-header">
                    <img src={logo} className="App-logo" alt="logo" />
                    <p className='inputs'>
                    Welcome to the Science Spelling Training.<br/><br/>
                    Please enter your name:
                    </p>
                    <div className='name-input-container'>
                        <WhiteTextField id="name-input" value={this.state.name}
                        fullWidth label="Name" variant="outlined" 
                        onChange={this.handleNameChange.bind(this)} onKeyPress={this.handleEnter.bind(this)}/>
                    </div>

                    <ThemeProvider theme={theme}>
                        <Button variant="contained" color="primary"
                        size='large' disabled={!isDisabled}
                        onClick={this.handleButtonClick.bind(this)}>
                            Continue
                        </Button>
                </ThemeProvider>
                </header>
            </div>
        );
    }
}

export const LandingPageView = withRouter(LandingPage);
