import React from 'react';
import {withRouter} from 'react-router';
import {MediaComponentLayout} from './mediaComponet/layout';
import {wordList} from '../../wordList';
import { WhiteTextField } from '../../assets/inputTypes/inputTypes'
import Button from '@material-ui/core/Button';
import { ThemeProvider } from '@material-ui/styles';
import { defaultTheme } from "../../assets/inputTypes/themes.js";
import { validateAlpha } from '../../utils/validations';


class WordComponent extends React.Component {
    constructor(props){
        super(props);
        this.state = {currentWord: 0, isDisabled: false, typedAnswer: ''}
    }



    handleNameChange(event) {
        this.setState({ name: event.target.value });
    };

    handleEnter(event) {
        const errors = validateAlpha(this.state.name);
        const isDisabled = Object.keys(errors).some(x => errors[x]);

        if (event.key === 'Enter' && isDisabled) {
            this.setState({ name: event.target.value });
            this.props.history.push('/introduction');
        }
    }

    handleButtonClick() {
        this.props.history.push('/introduction');
    }

    render() {
        const {currentWord, isDisabled, typedAnswer} = this.state;
        return (
            <div>
                <MediaComponentLayout word={wordList[currentWord]} soundFinished={isDisabled} />
                <div className='name-input-container'>
                        <WhiteTextField id="name-input" value={typedAnswer}
                        fullWidth label="Name" variant="outlined" 
                        onChange={this.handleNameChange.bind(this)} onKeyPress={this.handleEnter.bind(this)}/>
                    </div>

                    <ThemeProvider theme={defaultTheme}>
                        <Button variant="contained" color="primary"
                        size='large' disabled={!isDisabled}
                        onClick={this.handleButtonClick.bind(this)}>
                            Continue
                        </Button>
                </ThemeProvider>
            </div>
        );
    }
}

export const WordComponentView = withRouter(WordComponent);
