import React from 'react';
import {withRouter} from 'react-router';
import Sound from 'react-sound';

class MediaComponent extends React.Component {
    constructor(props){
        super(props);
        this.images = this.importAll(require.context('../../../assets/images/wordImages', false, /\.(jpe?g)$/));
        this.sounds = this.importAll(require.context('../../../assets/audioFiles/pretest_talker', false, /\.wav$/));

        console.log(this.images)
        console.log(this.sounds)
    }

    componentDidMount() {}

    importAll(r) {
        let images = {};
        // eslint-disable-next-line array-callback-return
        r.keys().map((item, index) => {images[item.replace('','')] = r(item) });
        return images;
    }

    render() {
        const {word} = this.props;
        console.log(`./pic_${word}.jpg`);
        return (
            <div>
                <img src={this.images[`./pic_${word}.jpg`]} alt='' />
                {/* <Sound */}
                        {/* url={this.state.showIntro ? instructions : questions} */}
                        {/* playStatus={this.state.playingStatus} */}
                        {/* onFinishedPlaying={this.handleIntroFinished.bind(this)} */}
                    {/* /> */}
            </div>
        );
    }
}

export const MediaComponentView = withRouter(MediaComponent);
